import React, { Component } from "react";
import API from "./utils/API.js"
import Chart from "react-apexcharts";
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      search: "",
      results: [],
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998]
        }
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70, 91]
        }
      ]
    };
  }

  searchWeather = query => {
    API.search(query)
    .then(res=>console.log(res))
  }

  handleButtonSelect = event => {
    event.preventDefault();
    console.log(event);
    const value = event.target.value;
    this.setState({search: value});
    this.searchWeather(this.state.search);
  }

  render() {
    return (
      <div className="app">
        <DropdownButton id="dropdown-basic-button" title="Select a City">
          <Dropdown.Item value="">Atlanta USA</Dropdown.Item>
          <Dropdown.Item value="London,UK">London England</Dropdown.Item>
          <Dropdown.Item value="Bujumbura,Burundi" onClick={this.handleButtonSelect}>Bujumbura Burundi</Dropdown.Item>
        </DropdownButton>
        <div className="row">
          <div className="mixed-chart">
            <Chart
                options={this.state.options}
                series={this.state.series}
                type="line"
                width="500"
              />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
